const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

const initialState = {
    users: [
        {id: 1, photoUrl: 'https://vignette.wikia.nocookie.net/p__/images/e/e0/Ted_Mosby.jpg/revision/latest?cb=20121009145019&path-prefix=protagonist', followed: true, status: 'status1', fullname: 'name1 sur1', location: {city: 'city1', country: 'country1'}},
        {id: 2, followed: false, status: 'status2', fullname: 'name2 sur2', location: {city: 'city2', country: 'country2'}},
        {id: 3, followed: false, status: 'status3', fullname: 'name3 sur3', location: {city: 'city3', country: 'country3'}}
    ]
};

let usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW: {
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
             };
        }

        case UNFOLLOW: {
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        }

        case SET_USERS:
            return {
            ...state,
                users: [
                    ...state.users,
                    ...action.users
                ]
        }

        default:
            return state;
    }
};

export const followAC = (userId) => ({type: FOLLOW, userId});

export const unfollowAC = (userId) => ({type: UNFOLLOW, userId});

export const setUsersAC = (users) => ({type: SET_USERS, users });

export default usersReducer;
